package com;
public class Strings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String invite = "AJITH";
		String txt = "AJITHKUMAR";
		String firstName = "AJITH";
		String lastName = "KUMAR";
		int x = 3;
		int y = 6;
		int z = x+y;
		String a = "69";
		int b = 97;
		String c = a+b;
		
		System.out.println(invite+" , "+txt.length()+" , "+txt.toUpperCase()+" , "+txt.toLowerCase()+" , "+txt.indexOf("U")+" , "+(firstName+""+lastName)+" , "+z+" , "+c);
	}

}
