package com;

 public class operators {
 
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10,y = 2;
		
		System.out.println(x+y+" , "+x*y+" , "+x/y+" , "+x%y+" , "+(++x)+" , "+(--y)+" , "+(x==y)+" , "+(x!=y)+" , "+(x<y)+" , "+(x>y)+" , "+(x<=y)+" , "+(x>=y));
 		
	}

}
